import './Dashboard.css'
import React, { useContext, useEffect, useState } from 'react'
import {
  Link,
  useNavigate,
  useLocation,
  Navigate,
  useParams,
} from 'react-router-dom'
import { Dialog, Input, TextareaAutosize, TextField } from '@mui/material'
import Button from '@mui/material/Button'
import { styled } from '@mui/material/styles'
import DialogTitle from '@mui/material/DialogTitle'
import DialogContent from '@mui/material/DialogContent'
import DialogActions from '@mui/material/DialogActions'
import IconButton from '@mui/material/IconButton'
import CloseIcon from '@mui/icons-material/Close'
import Typography from '@mui/material/Typography'
import axios from 'axios'
import './Dashboard.css'
import userNotesContext from '../../Context/userNotesContext'
import Chip from '@mui/material/Chip'
import DeleteIcon from '@mui/icons-material/Delete'
import { convertFromRaw, convertToRaw, EditorState } from 'draft-js'
import ModalEditor from '../../Hooks/ModalEditor'

const Note = ({ note }) => {
  const location = useLocation()
  // const { id } = useParams()
  // const navigate = useNavigate()
  // const [Note, setNote] = useState({
  //   id: note._id,
  //   title: note.title,
  //   body: note.body,
  // })
  const [editorStateTitle, setEditorStateTitle] = useState(() =>
    EditorState.createWithContent(convertFromRaw(JSON.parse(note.title)))
  )
  const [editorStateBody, setEditorStateBody] = useState(() =>
    EditorState.createWithContent(convertFromRaw(JSON.parse(note.body)))
  )
  const { CheckIfNoteUpdated, setCheckIfNoteUpdated } =
    useContext(userNotesContext)

  useEffect(() => {
    // if (CheckIfNoteUpdated) setCheckIfNoteUpdated(false)
  }, [CheckIfNoteUpdated])

  return (
    <section className='single-note'>
      <Link
        key={note._id}
        to={`/api/dashboard/${note._id}`}
        state={{ background: location }}
        className='note-link'
      >
        {/* <div className='note-title'> {note.title}</div>
        <div className='note-body'> {note.body}</div> */}
        <div className='note-title'>
          <ModalEditor
            editorState={editorStateTitle}
            onChange={setEditorStateTitle}
            isReadOnly={true}
            setCheckIfNoteUpdated={setCheckIfNoteUpdated}
          />
        </div>
        <div className='note-body'>
          <ModalEditor
            editorState={editorStateBody}
            onChange={setEditorStateBody}
            isReadOnly={true}
            setCheckIfNoteUpdated={setCheckIfNoteUpdated}
          />
        </div>
      </Link>
    </section>
  )
}

export default Note
