import './Dashboard.css'
import React, { useEffect, useState } from 'react'
import { useNavigate, Outlet, Link, useLocation } from 'react-router-dom'
import axios from 'axios'
import { useTheme } from '@mui/material/styles'
import Navbar from './Navbar'
import Note from './Note'
import { experimentalStyled as styled } from '@mui/material/styles'
import Box from '@mui/material/Box'
import Paper from '@mui/material/Paper'
import Grid from '@mui/material/Grid'
import Modal from './Modal'
function Dashboard() {
  const theme = useTheme()
  let location = useLocation()
  const navigate = useNavigate()
  const [Notes, setNotes] = useState()
  const verifyUser = async () => {
    try {
      const response = await axios.get('/api/dashboard/', {
        withCredentials: true,
      })
      if (response?.status === 200) {
        setNotes(response?.data?.notes)
      }
    } catch (err) {
      console.log('error from dashboard js file', err)
      navigate('/login')
    }
  }

  useEffect(() => {
    verifyUser()
  }, [])

  const [CheckIfNoteUpdated, setCheckIfNoteUpdated ] = useState(false)

  useEffect(()=>{
    console.log('CheckIfNoteUpdated', CheckIfNoteUpdated);
    if(CheckIfNoteUpdated) setCheckIfNoteUpdated(false)
  },[CheckIfNoteUpdated])

  const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }))

  return (
    <>
      <div className='notes-wrapper'>
        <Navbar />
        <Outlet />
        <div>New Note</div>
        <div className='notes-container'>
          {Notes?.map((note) => {
            return (
              // <Link
              //   to={`/api/dashboard/${note._id}`}
              //   key={note._id}
              //   state={{ background: location }}
              //   style={null}
              // >
                <Note key={note._id} note={note} checkIfNoteUpdated={setCheckIfNoteUpdated}/>
              //  </Link>
            )
          })}
        </div>
      </div>
    </>
  )

  // return (
  //   <>
  //     <Navbar />

  //     <div className='notes-wrapper'>
  //       <Box sx={{ flexGrow: 1 }}>
  //         <Grid
  //           container
  //           spacing={{ xs: 2, md: 3 }}
  //           columns={{ xs: 4, sm: 8, md: 12 }}
  //         >
  //           {Notes?.map((note) => {
  //             return (
  //               <Grid item xs={2} sm={4} md={4} key={note._id}>
  //                 <Item>
  //                  <Note note={note}/>
  //                 </Item>
  //               </Grid>
  //             )
  //           })}
  //         </Grid>
  //       </Box>
  //       <hr />
  //       aqdr
  //       {/* <div style={{height:'150px' ,width:'150px' ,borderRadius:"10px", display:'flex', justifyContent:'center', border:'10px solid red'}}> */}
  //       {/* </div> */}
  //     </div>
  //     {/* <Outlet /> */}
  //   </>
  // )
}

export default Dashboard
