import React, { useContext, useEffect, useRef, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import { Dialog } from '@reach/dialog'
import '@reach/dialog/styles.css'
import axios from 'axios'
import DialogContent from '@mui/material/DialogContent'
import { TextareaAutosize } from '@mui/material'
import userNotesContext from '../../Context/userNotesContext'
import ContentEditable from 'react-contenteditable'
import sanitizeHtml from 'sanitize-html'
import sanitize from 'sanitize-html'
import './Dashboard.css'
const Modal = () => {
  let navigate = useNavigate()
  let { id } = useParams()
  let buttonRef = useRef(null)
  const { setCheckIfNoteUpdated } = useContext(userNotesContext)
  const [CheckIfNoteIsEdited, setCheckIfNoteIsEdited] = useState(false)

  const onDismiss = async () => {
    if (CheckIfNoteIsEdited) {
      await axios.put(`/api/dashboard/notes/${id}`, {
        title: Note.title,
        body: Note.body,
      })
      setCheckIfNoteUpdated(true)
      setCheckIfNoteIsEdited(false)
    }

    navigate(-1)
  }
  const [Note, setNote] = useState({
    id: id,
    title: '',
    body: '',
  })

  const fetchNote = async (id) => {
    try {
      const response = await axios.get(`/api/dashboard/${id}`, {
        withCredentials: true,
      })
      if (response?.status === 200) {
        setNote({
          title: response?.data?.note?.title,
          body: response?.data?.note?.body,
        })
      }
    } catch (err) {
      console.log('error from dashboard js file', err)
      navigate('/login')
    }
  }
  const onChange = (e) => {
    setNote((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }))
    setCheckIfNoteIsEdited(true)
  }
  console.log(CheckIfNoteIsEdited)

  useEffect(() => {
    fetchNote(id)
  }, [])

  if (!id) return null
  return (
    <Dialog
      aria-labelledby='label'
      onDismiss={onDismiss}
      initialFocusRef={buttonRef}
      // style={{padding: '0px'}}
    >
      {/* <div
        style={
          {
            // display: 'grid',
            // justifyContent: 'center',
            // padding: '8px 8px',
            // border: '1px solid pink',
          }
        }
      > */}
        <ContentEditable
          className='title-contenteditable'
          html={Note.title}
          onChange={(e) =>
            onChange({ target: { name: 'title', value: e.target.value } })
          }
          name='title'
          placeholder='Title'
          defaultValue={Note.title}
        />
        <ContentEditable
        className='body-contenteditable'
          // style={{
          //   padding: '0rem',
          //   margin: '0rem',
          //   fontSize: '1.5rem',
          //   width: '100%',
          // }}
          html={Note.body}
          onChange={(e) =>
            onChange({ target: { name: 'body', value: e.target.value } })
          }
          name='body'
          placeholder='Type your note here'
          defaultValue={Note.title}
        />
        {/* <TextareaAutosize
          aria-label='Enter Title'
          // minRows={3}
          style={{
            padding: '0.7rem',
            fontSize: '1.5rem',
            width: '100%',
            boxSizing: 'border-box',
          }}
          name='title'
          placeholder='Title'
          onChange={onChange}
          defaultValue={Note?.title}
        /> */}
        {/* <DialogContent dividers> */}
        {/* <TextareaAutosize
          aria-label='Enter Body'
          placeholder='Body'
          minRows={10}
          name='body'
          onChange={onChange}
          defaultValue={Note?.body}
          style={{ width: '100%', boxSizing: 'border-box' }}
        /> */}
        {/* </DialogContent> */}
      {/* </div> */}
    </Dialog>
  )
}

export default Modal
