import React from 'react'
import { RichUtils } from 'draft-js'
import FormatBoldIcon from '@mui/icons-material/FormatBold'
import { IconButton } from '@mui/material'
import FormatItalicIcon from '@mui/icons-material/FormatItalic'
import FormatUnderlinedIcon from '@mui/icons-material/FormatUnderlined'
import CodeIcon from '@mui/icons-material/Code'
import FormatListBulletedIcon from '@mui/icons-material/FormatListBulleted'
import FormatListNumberedIcon from '@mui/icons-material/FormatListNumbered'
import DeleteIcon from '@mui/icons-material/Delete'
import SaveIcon from '@mui/icons-material/Save'
import ClearIcon from '@mui/icons-material/Clear'

const Toolbar = (props) => {
  const inlineStyles = [
    { type: 'BOLD', label: 'Bold', toolTip: 'line', icon: <FormatBoldIcon /> },
    {
      type: 'ITALIC',
      label: 'Italic',
      toolTip: 'Italic',
      icon: <FormatItalicIcon />,
    },
    {
      type: 'UNDERLINE',
      label: 'Underline',
      toolTip: 'Underline',
      icon: <FormatUnderlinedIcon />,
    },
    {
      type: 'CODE',
      label: 'Code',
      toolTip: 'Code Block',
      icon: <CodeIcon />,
    },
  ]

  const blockStyles = [
    {
      type: 'unordered-list-item',
      label: 'Unordered List',
      toolTip: 'Unordered List',
      icon: <FormatListBulletedIcon />,
    },
    {
      type: 'ordered-list-item',
      label: 'Ordered List',
      toolTip: 'Ordered List',
      icon: <FormatListNumberedIcon />,
    },
  ]

  const {
    editorState,
    setEditorState,
    switchFor,
    submitNote,
    handleDelete,
    clearNote,
  } = props

  const handleInlineStyle = (event, style) => {
    event.preventDefault()
    setEditorState(RichUtils.toggleInlineStyle(editorState, style))
  }

  const handleBlockStyle = (event, block) => {
    event.preventDefault()
    setEditorState(RichUtils.toggleBlockType(editorState, block))
  }

  const renderInlineStyleButton = (style, index) => {
    const currentInlineStyle = editorState.getCurrentInlineStyle()
    let className = 'toolbar-button'
    if (currentInlineStyle.has(style.type)) {
      className = 'toolbar-button-selected'
    }

    return (
      <IconButton
        key={index}
        title={style.toolTip}
        onMouseDown={(event) => handleInlineStyle(event, style.type)}
        onClick={(event) => event.preventDefault()}
        className={className}
      >
        {style.icon}
      </IconButton>
    )
  }

  const renderBlockStyleButton = (block, index) => {
    const currentBlockType = RichUtils.getCurrentBlockType(editorState)
    let className = 'toolbar-button'
    if (currentBlockType === block.type) {
      className = 'toolbar-button-selected'
    }

    return (
      <IconButton
        key={index}
        title={block.toolTip}
        onMouseDown={(event) => handleInlineStyle(event, block.type)}
        onClick={(event) => event.preventDefault()}
        className={className}
      >
        {block.icon}
      </IconButton>
    )
  }

  const editorToolbar = {
    // border: '1px solid #ccc',
    display: 'flex',
    justifyContent: 'space-between',
    boxShadow:
      'rgba(60, 64, 67, 0.3) 0px 1px 2px 0px, rgba(60, 64, 67, 0.15) 0px 1px 3px 1px',
  }
  const toolbarLeft = {}
  const toolbarRight = {}

  return (
    <div style={editorToolbar}>
      <div style={toolbarLeft}>
        {inlineStyles.map((style, index) => {
          return renderInlineStyleButton(style, index)
        })}
        {blockStyles.map((block, index) => {
          return renderBlockStyleButton(block, index)
        })}
      </div>
      <div className={toolbarRight}>
        {switchFor === 'addNote' && (
          <>
            <IconButton onClick={submitNote} title='Save'>
              <SaveIcon />
            </IconButton>
            <IconButton onClick={clearNote} title='Clear'>
              <ClearIcon />
            </IconButton>
          </>
        )}
        {switchFor === 'modaleditor' && (
          <>
            <IconButton onClick={handleDelete} title='Delete'>
              <DeleteIcon />
            </IconButton>
          </>
        )}
      </div>
    </div>
  )
}

export default Toolbar
