import axios from 'axios'
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { Button, Grid, TextField, Box } from '@mui/material'
import AccountCircle from '@mui/icons-material/AccountCircle'
import LockIcon from '@mui/icons-material/Lock'
import LoginIcon from '@mui/icons-material/Login'

function Login({ setToken }) {
  const navigate = useNavigate()
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  })
  const [error, setError] = useState('')
  const { email, password } = formData
  const onChange = (e) => {
    setError('')
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }))
  }

  const loginUser = async (email, password) => {
    try {
      const data = await axios.post('http://localhost:3000/api/login', {
        email,
        password,
      })
      return data
    } catch (error) {
      setError(error.response.data.message)
    }
  }

  const onSubmit = async (e) => {
    e.preventDefault()
    if (email === '' || password === '') {
      setError('Please enter all fields')
      return
    }
    const response = await loginUser(email, password)
    console.log(response)
  }
  const handleSubmit = async (e) => {
    e.preventDefault()
    //TODO: install axios and set up post req
    // await axios
    //   .post('http://localhost:3000/api/login', {
    //     email: email,
    //     password: password,
    //   })
    //   .then((response) => {
    //     const data = response.data
    //     if (data.user) {
    //       localStorage.setItem('token', data.user)
    //       navigate(`/dashboard`)
    //     } else {
    //       alert('Please check the credentials')
    //     }
    //   })
    const response = await loginUser(email, password)
    // if (response.data.status === 'ok') setToken(response.data.user)
    if (response.data.status === 'ok') {
      localStorage.setItem('token', response.data.user)
      navigate('/dashboard')
    } else alert('Please check the credentials')
  }
  return (
    <section
      style={{
        boxSizing: 'border-box',
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor:'#f5f5f5',
      }}
    >
      <form onSubmit={onSubmit}>
        <Grid
          container
          direction='column'
          alignItems='center'
          justifyContent='center'
          sx={{
            width: {
              xs: 350,
              sm: 400,
            },
            height: {
              xs: 500,
              sm: 500,
            },
            boxShadow:1,
          }}
        >
          <h1>Login</h1>
          <Grid container direction='column' alignItems='center'>
            <Box sx={{ display: 'flex', alignItems: 'flex-end', p: 0.5, m: 1 }}>
              <AccountCircle sx={{ color: 'action.active', mr: 1, my: 0.5 }} />
              <TextField
                id='input-with-sx'
                type='email'
                label='Email'
                variant='standard'
                name='email'
                value={email}
                onChange={onChange}
                sx={{
                  width: {
                    xs: 220,
                    sm: 280,
                  },
                }}
              />
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'flex-end', p: 0.5, m: 1 }}>
              <LockIcon sx={{ color: 'action.active', mr: 1, my: 0.5 }} />
              <TextField
                id='input-with-sx'
                type='password'
                label='Password'
                variant='standard'
                name='password'
                value={password}
                onChange={onChange}
                sx={{
                  width: {
                    xs: 220,
                    sm: 280,
                  },
                }}
              />
            </Box>

            <Button
              type='submit'
              size='medium'
              variant='contained'
              endIcon={<LoginIcon />}
              sx={{ width: 120, mt: 2 }}
              disabled={!email || !password}
            >
              Login{' '}
            </Button>
            <div style={{ height: '20px', marginTop: '1rem' }}>{error}</div>
          </Grid>
        </Grid>
      </form>
      {/* <form onSubmit={onSubmit}>
        <input
          type='email'
          name='email'
          placeholder='Enter email'
          value={email}
          onChange={onChange}
        />
        <input
          type='password'
          name='password'
          placeholder='Enter password'
          value={password}
          onChange={onChange}
        />
        {/* <Button variant='contained'>Hello World</Button>; */}
      {/* <button type='submit'> Login</button> }
      </form> */}

      {/* <form>
        <label>Email</label>
        <input type='text' onChange={(e) => setEmail(e.target.value)} />
        <label>Passowrd</label>
        <input type='password' onChange={(e) => setPassword(e.target.value)} />
        <button onClick={handleSubmit}>Submit</button>
      </form> */}
    </section>
  )
}

export default Login
