    //   if (user) {
    //     populateNotes(token)
    //   } else {
    //     localStorage.removeItem('token')
    //     navigate('/login')
    //   }
    // }
  }, [])
  return (
    <>
      <Navbar />
      {Notes &&
        Notes.map((note) => {
          return (
            <div key={note._id}>
              Title: {note.title}
              Body: {note.body}
            </div>
          )
        })}
    </>
  )
}

export default Dashboard
