import axios from 'axios'
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import PropTypes from 'prop-types'
import { Button, FormControl, Grid, TextField } from '@mui/material'
import InputAdornment from '@mui/material/InputAdornment'
import AccountCircle from '@mui/icons-material/AccountCircle'
import LockIcon from '@mui/icons-material/Lock'
function Login({ setToken }) {
  const navigate = useNavigate()
  // const [email, setEmail] = useState('')
  // const [password, setPassword] = useState('')
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  })
  const [error, setError] = useState('')
  const { email, password } = formData

  const onChange = (e) => {
    setError('')
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }))
  }

  const loginUser = async (email, password) => {
    try {
      const data = await axios.post('http://localhost:3000/api/login', {
        email,
        password,
      })
      return data
    } catch (error) {
      setError(error.response.data.message)
    }
  }

  const onSubmit = async (e) => {
    e.preventDefault()
    if (email === '' || password === '') {
      setError('Please enter all fields')
      return
    }
    const response = await loginUser(email, password)
    console.log(response)
  }
  const handleSubmit = async (e) => {
    e.preventDefault()
    //TODO: install axios and set up post req
    // await axios
    //   .post('http://localhost:3000/api/login', {
    //     email: email,
    //     password: password,
    //   })
    //   .then((response) => {
    //     const data = response.data
    //     if (data.user) {
    //       localStorage.setItem('token', data.user)
    //       navigate(`/dashboard`)
    //     } else {
    //       alert('Please check the credentials')
    //     }
    //   })
    const response = await loginUser(email, password)
    // if (response.data.status === 'ok') setToken(response.data.user)
    if (response.data.status === 'ok') {
      localStorage.setItem('token', response.data.user)
      navigate('/dashboard')
    } else alert('Please check the credentials')
  }
  return (
    <section
    // style={{boxSizing:'border-box', minHeight: '100vh', padding: 10, border: '3px solid red' }}
    >
      <form>
        <Grid
          container
          direction='column'
          alignItems='center'
          justifyContent='center'
          style={{ minHeight: '100vh', border: '3px solid teal' }}
        >
          <TextField
            id='input-with-icon-textfield'
            label='Email'
            sx={{
              mx: 'auto',
              width: {
                sm:200,
                md:300,
              },
              p: 1,
              m: 1,
            }}
            // style={{ width: '40%' , border: '1px solid red'}}
            InputProps={{
              startAdornment: (
                <InputAdornment position='start'>
                  <AccountCircle />
                </InputAdornment>
              ),
            }}
            variant='standard'
          />
          <TextField
            id='input-with-icon-textfield'
            label='Password'
            InputProps={{
              startAdornment: (
                <InputAdornment position='start'>
                  <LockIcon />
                </InputAdornment>
              ),
            }}
            variant='standard'
          />
        </Grid>
      </form>
      {/* <form onSubmit={onSubmit}>
        <input
          type='email'
          name='email'
          placeholder='Enter email'
          value={email}
          onChange={onChange}
        />
        <input
          type='password'
          name='password'
          placeholder='Enter password'
          value={password}
          onChange={onChange}
        />
        {/* <Button variant='contained'>Hello World</Button>; */}
      {/* <button type='submit'> Login</button> }
      </form> */}
      {error}
      {/* <form>
        <label>Email</label>
        <input type='text' onChange={(e) => setEmail(e.target.value)} />
        <label>Passowrd</label>
        <input type='password' onChange={(e) => setPassword(e.target.value)} />
        <button onClick={handleSubmit}>Submit</button>
      </form> */}
    </section>
  )
}

export default Login
