import React, { useState } from 'react'
import {
  convertFromHTML,
  convertToRaw,
  Editor,
  EditorState,
  RichUtils,
} from 'draft-js'
import Toolbar from '../../Hooks/Toolbar'
import axios from 'axios'
import './Dashboard.css'
export default function AddNote() {
  const [editorState, setEditorState] = useState(() =>
    EditorState.createEmpty()
  )
  const [checkIfNoteEdited, setCheckIfNoteEdited] = useState(false)
  const handleKeyCommand = (command, editorState) => {
    const newState = RichUtils.handleKeyCommand(editorState, command)
    if (newState) {
      setEditorState(newState)
      setCheckIfNoteEdited(true)
      return 'handled'
    }
    return 'not handled'
  }
  const submitNote = async () => {
      console.log(editorState.getCurrentContent().getPlainText());
    // if (checkIfNoteEdited) {
    //   await axios.post('/api/dashboard/notes', {
    //     body: JSON.stringify(convertToRaw(editorState.getCurrentContent())),
    //   })
    //   console.log('note submitted');
    //   setCheckIfNoteEdited(false)
    // }
  }
  const onBlur = async () => {
    await submitNote()
    console.log('blur')
  }
  const onChange = (newState) => {
    const currentContentState = editorState.getCurrentContent()
    const newContentState = newState.getCurrentContent()

    setEditorState(newState)
    if (currentContentState !== newContentState) {
    //   if (newState.getCurrentContent().hasText() ) {
    //   if (newState.getCurrentContent().hasText() ) {
        setCheckIfNoteEdited(true)
    //   }
      console.log('chaning')
    } else {
      console.log('not changing')
    }
  }

  return (
    <section className='addNote-wrapper'>
      <div className='addNote-container'>
        <Editor
          editorState={editorState}
          value={editorState}
          onChange={onChange}
          //   onChange={setEditorState}
          handleKeyCommand={handleKeyCommand}
        //   onBlur={onBlur}
          className='addNote-editor'
        />
        <Toolbar editorState={editorState} setEditorState={setEditorState} />
        <button onClick={submitNote}> Save</button>
      </div>
    </section>
  )
}
