import './Dashboard.css'
import React, { useState } from 'react'
import {
  Link,
  useNavigate,
  useLocation,
  Navigate,
  useParams,
} from 'react-router-dom'

import { Dialog, Input, TextareaAutosize, TextField } from '@mui/material'
import Button from '@mui/material/Button'
import { styled } from '@mui/material/styles'
import DialogTitle from '@mui/material/DialogTitle'
import DialogContent from '@mui/material/DialogContent'
import DialogActions from '@mui/material/DialogActions'
import IconButton from '@mui/material/IconButton'
import CloseIcon from '@mui/icons-material/Close'
import Typography from '@mui/material/Typography'
import axios from 'axios'

const BootstrapDialog = styled(Dialog)(({ theme }) => ({
  '& .MuiDialogContent-root': {
    // padding: theme.spacing(0),
  },
  '& .MuiDialogActions-root': {
    // padding: theme.spacing(0),
  },
}))

const BootstrapDialogTitle = (props) => {
  const { children, onClose, ...other } = props

  return (
    <DialogTitle sx={{ m: 0, p: 0 }} {...other}>
      {children}
      {onClose ? (
        <IconButton
          aria-label='close'
          onClick={onClose}
          sx={{
            position: 'absolute',
            right: 8,
            top: 8,
            color: (theme) => theme.palette.grey[500],
          }}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </DialogTitle>
  )
}
const Note = ({ note }) => {
  // const location = useLocation()
  // const { id } = useParams()
  // const navigate = useNavigate()
  const [Note, setNote] = useState({
    id: note._id,
    title: note.title,
    body: note.body,
  })

  const [open, setOpen] = React.useState(false)
  const onChange = (e) => {
    console.log('note', Note.body)
    setNote((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }))
  }
  const handleClickOpen = () => {
    setOpen(!open)
  }
  const handleClose = async () => {
    //TODO update note
    // const resp = await axios.put(`/api/dashboard/notes/${Note.id}`, {
    //   title: Note.title,
    //   body: Note.body,
    // })
    // console.log('resp', resp)
    console.log('closed')
    setOpen(false)
  }

  return (
    // <div className='note-container' onClick={(e) => onNoteClick(_id)}>
    <div>
      <Button variant='outlined' onClick={handleClickOpen}>
        Open dialog
      </Button>
      <BootstrapDialog
        onClose={handleClose}
        aria-labelledby='customized-dialog-title'
        open={open}
        // maxWidth="md"
        fullWidth
      >
        <div style={{  boxSizing: 'border-box' }}>
          <TextareaAutosize
            aria-label='empty textarea'
            // minRows={3}
            style={{ padding: '1rem', fontSize: '1.5rem',width:'95%' }}
            name='title'
            placeholder='Title'
            onChange={onChange}
            defaultValue={Note.title}
          />
          <DialogContent dividers>
            <TextareaAutosize
              aria-label='empty textarea'
              placeholder='Body'
              minRows={10}
              name='body'
              onChange={onChange}
              defaultValue={Note.body}
              // style={{ width: '100%', boxSizing: 'border-box' }}
            />
          </DialogContent>
        </div>
        <DialogActions>
          {/* <Button autoFocus onClick={handleClose}>
            Save changes
          </Button> */}
        </DialogActions>
      </BootstrapDialog>
    </div>
  )
}

export default Note
